#   https://leetcode.com/problems/3sum-with-multiplicity

#   https://leetcode.com/problems/3sum-with-multiplicity/solution
#   https://leetcode.com/problems/3sum-with-multiplicity/discuss/1123612/C%2B%2B-Super-Simple-7-line-Solution-Without-Sorting


from collections import Counter
from functools import reduce
from typing import List
import operator as op


def nCr(n, r):
    r = min(r, n - r)
    numerator = reduce(op.mul, range(n, n - r, -1), 1)
    denominator = reduce(op.mul, range(1, r + 1), 1)
    return numerator // denominator


class Solution:
    #   https://leetcode.com/explore/challenge/card/march-leetcoding-challenge-2021/591/week-4-march-22nd-march-28th/3682
    #   runtime; 84ms, 59.77%
    #   memory; 14.7MB
    def threeSumMulti(self, arr: List[int], target: int) -> int:
        c, s, cnt = Counter(arr), set(), 0
        keys = sorted(c.keys())
        for i, a in enumerate(keys):
            startNum = i if c[a] > 1 else i + 1
            t = target - a
            for j in range(startNum, len(keys)):
                cand = t - keys[j]
                if cand not in c:
                    continue
                if (a == cand == keys[j] and c[a] > 2) or (a != cand == keys[j] and c[cand] > 1) or keys[j] < cand:
                    #print(a, keys[j], cand, c[cand])
                    s.add((a, keys[j], cand))
        for i, j, k in s:
            if i != j != k:
                cnt += c[i] * c[j] * c[k]
                #print(i, j, k, c[i] * c[j] * c[k])
            elif i == j == k:
                cnt += nCr(c[i], 3)
                #print(i, j, k, nCr(c[i], 3))
            else:
                if i == j:
                    cnt += nCr(c[i], 2) * c[k]
                    #print(i, j, k, nCr(c[i], 2) * c[k])
                elif i == k:
                    cnt += nCr(c[i], 2) * c[j]
                    #print(i, j, k, nCr(c[i], 2) * c[j])
                elif j == k:
                    cnt += nCr(c[j], 2) * c[i]
                    #print(i, j, k, nCr(c[j], 2) * c[i])
        return cnt % (10 ** 9 + 7)


s = Solution()
