#   https://leetcode.com/problems/maximum-length-of-repeated-subarray
#   https://leetcode.com/problems/maximum-length-of-repeated-subarray/solution
#   https://leetcode.com/explore/challenge/card/july-leetcoding-challenge-2021/609/week-2-july-8th-july-14th/3807


from collections import defaultdict
from typing import List


class Solution:
    #   Time Limit Exceeded
    def findLength0(self, nums1: List[int], nums2: List[int]) -> int:
        if nums1 is None or 0 == len(nums1) or nums2 is None or 0 == len(nums2):
            return 0
        d1, d2 = defaultdict(list), defaultdict(list)
        for i, n in enumerate(nums1):
            d1[n].append(i)
        for i, n in enumerate(nums2):
            d2[n].append(i)
        maxLen, l1, l2 = 0, len(nums1), len(nums2)
        for n, idxList1 in d1.items():
            for i1 in idxList1:
                if l1 - i1 < maxLen:
                    break
                for i2 in d2[n]:
                    if l2 - i1 < maxLen:
                        break
                    minLen = min(l1 - i1, l2 - i2)
                    while 0 < minLen:
                        if minLen < maxLen:
                            break
                        if nums1[i1:i1 + minLen] == nums2[i2:i2 + minLen]:
                            maxLen = max(maxLen, minLen)
                        minLen -= 1
        return maxLen

    #   Time Limit Exceeded
    def findLength1(self, nums1: List[int], nums2: List[int]) -> int:
        if nums1 is None or 0 == len(nums1) or nums2 is None or 0 == len(nums2):
            return 0
        grid = [[False] * len(nums2) for _ in range(len(nums1))]
        for r, n1 in enumerate(nums1):
            for c, n2 in enumerate(nums2):
                if n1 == n2:
                    grid[r][c] = True
        maxLen, R, C = 0, len(grid), len(grid[0])
        for r in range(R):
            for c in range(C):
                if False == grid[r][c]:
                    continue
                if R - r < maxLen or C - c < maxLen:
                    break
                rr, cc, curLen = r, c, 0
                while rr < R and cc < C and grid[rr][cc]:
                    curLen += 1
                    rr += 1
                    cc += 1
                maxLen = max(maxLen, curLen)
        return maxLen

    #   runtime; 5156ms, 23.97%
    #   memory; 39.5MB, 59.38%
    def findLength(self, nums1: List[int], nums2: List[int]) -> int:
        if nums1 is None or 0 == len(nums1) or nums2 is None or 0 == len(nums2):
            return 0
        grid = [[0] * len(nums2) for _ in range(len(nums1))]
        maxLen, R, C = 0, len(grid), len(grid[0])
        for r in range(R):
            if nums1[r] == nums2[0]:
                grid[r][0] = 1
        for c in range(C):
            if nums1[0] == nums2[c]:
                grid[0][c] = 1
        for r in range(1, R):
            for c in range(1, C):
                if nums1[r] == nums2[c]:
                    grid[r][c] = grid[r - 1][c - 1] + 1
        for r in range(R):
            for c in range(C):
                maxLen = max(maxLen, grid[r][c])
        return maxLen


